import React, { Component } from "react";
import { connect } from "react-redux";
import * as actions from "../store/actions/index";
import "./cart.css";
import Layout from "../layout/layout";
import LoginIcon from "../../assets/loginicon.png";
import { NavLink } from "react-router-dom";
import CartProduct from "./cartProduct/cartProduct";
import CartTotal from "./cartTotal/cartTotal";
import Spinner from "../ui/spinner/spinner";

class Cart extends Component {
  componentDidMount() {
    this.props.onFetchOrders();
  }

  render() {
    let cartAuthenticated = (
      <div className="notLoggedIn">
        <h2>Ju nuk jeni kyçur akoma!</h2>
        <p>
          <NavLink to="/kycu">Kyçuni</NavLink> ose{" "}
          <NavLink to="/regjistrohu">krijoni</NavLink> një llogari për të
          vazhduar më tutje.
        </p>
        <img src={LoginIcon} alt="" />
      </div>
    );

    let cartProductList = <Spinner />;

    if (!this.props.loading) {
      cartProductList = this.props.orders.map((orderItem, i) => (
        <CartProduct
          key={i}
          productImage={orderItem.productImage}
          productTitle={orderItem.productTitle}
          productPrice={orderItem.productPrice}
        />
      ));
    }

    if (this.props.isAuthenticated) {
      cartAuthenticated = (
        <div className="cart">
          <div className="cartHeader">
            <h1>Shporta juaj</h1>
          </div>

          <div className="cartContainer">
            <h3>Produktet:</h3>
            <table id="cart">
              <tbody>
                <tr>
                  <th id="delTH"></th>
                  <th id="imageTH"></th>
                  <th className="productCell">
                    <h4>Produkti</h4>
                  </th>
                  <th id="priceTH">Cmimi</th>
                  <th id="countTH">Sasia</th>
                </tr>
                {cartProductList}
              </tbody>
            </table>

            <CartTotal />
            <button className="checkoutButton">Bëje porosinë</button>
          </div>
        </div>
      );
    }

    return <Layout>{cartAuthenticated}</Layout>;
  }
}

const mapStateToProps = (state) => {
  return {
    isAuthenticated: state.auth.token !== null,
    orders: state.orders.orders,
    loading: state.orders.loading,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    onFetchOrders: () => dispatch(actions.fetchOrders()),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Cart);
