import React, { Component } from "react";
import { connect } from "react-redux";
import Aux from "../hoc/Auxiliary";
import Product from "../../components/product/product";
import "./home.css";
import Axios from "axios";
import Spinner from "../ui/spinner/spinner";
import * as actionTypes from "../store/actions/actiontypes";

class Store extends Component {
  state = {
    product: null,
  };

  componentDidMount() {
    Axios.get("https://xpert-ecommerce.firebaseio.com/products.json").then(
      (response) => {
        this.setState({ product: [response.data] });
      }
    );
  }

  render() {
    let productList = <Spinner />;

    if (this.state.product) {
      productList = this.state.product.map((productItem, index) => (
        <Product
          key={index}
          productImage={productItem.img}
          productTitle={productItem.title}
          productDescription={productItem.description}
          productPrice={productItem.price}
        />
      ));
    }

    return (
      <Aux>
        <div>Slider</div>
        <div className="products">{productList}</div>
      </Aux>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    prods: state.product,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    onProductAdded: (prodId) =>
      dispatch({ type: actionTypes.ADD_PRODUCT, productID: prodId }),
    onProductRemoved: (prodId) =>
      dispatch({ type: actionTypes.REMOVE_PRODUCT, productID: prodId }),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Store);
