import React, { Component } from "react";
import { Link } from "react-router-dom";
import "./cartProduct.css";

class CartProduct extends Component {
  constructor(props) {
    super(props);
    this.state = { value: this.props.productCount };

    this.inputNumberHandler = this.inputNumberHandler.bind(this);
  }

  inputNumberHandler(event) {
    this.setState({
      value: event.target.value,
    });
  }

  productCountHandler(incOrDec) {
    let productCount = 1;
    switch (incOrDec) {
      case "increase":
        productCount++;
        break;
      case "decrease":
        productCount--;
        break;
      default:
        productCount = 1;
    }
  }

  render() {
    return (
      <tr className="cartProduct">
        <td>
          <button className="deleteButton"></button>
        </td>
        <td className="imageCell">
          <img src={this.props.productImage} alt="" />
        </td>
        <td className="productCell">
          <Link
            to={{
              pathname: `/products/${this.props.productId}`,
              state: this.props,
            }}
          >
            {this.props.productTitle}
          </Link>
        </td>
        <td>
          <h4>{this.props.productPrice} ALL</h4>
        </td>
        <td>
          <button onClick={this.productCountHandler("increase")}>+</button>
          <p>1</p>
          <button onClick={this.productCountHandler("decrease")}>-</button>
        </td>
      </tr>
    );
  }
}

export default CartProduct;
