import React, { Component } from "react";
import { Route } from "react-router-dom";
import "./product.css";

class Product extends Component {
  addToCartHandler = () => {
    <Route
      path="/shporta"
      render={() => <Product specifications={this.props} />}
    />;
  };

  render() {
    return (
      <div className="productContainer">
        <div>
          <img src={this.props.productImage} alt="" />
          <h3>{this.props.productTitle}</h3>
          <p>{this.props.productDescription}</p>
          <p className="price">ALL {this.props.productPrice}</p>
        </div>

        <div className="buttonDiv">
          <button>ADD TO CART</button>
          <button className="buyNow" onClick={this.addToCartHandler}>
            BUY NOW
          </button>
        </div>
      </div>
    );
  }
}

export default Product;
