import Axios from "axios";
import React, { Component } from "react";
import "./product.css";

class Product extends Component {
  state = {
    specifications: {
      ...this.props,
    },
    loading: false,
  };

  addToCartHandler = () => {
    this.setState({
      loading: true,
    });
    let productCartSpecifications = {};
    productCartSpecifications = this.state.specifications;
    Axios.post(
      "https://xpert-ecommerce.firebaseio.com/orders.json",
      productCartSpecifications
    )
      .then((response) => {
        this.setState({ loading: false });
      })
      .catch((error) => {});
  };

  render() {
    let productButtons = (
      <div className="buttonDiv">
        <button onClick={this.addToCartHandler}>ADD TO CART</button>
        <button className="buyNow">BUY NOW</button>
      </div>
    );

    if (this.state.loading) {
      productButtons = <p style={{ textAlign: "center" }}>Adding to cart...</p>;
    }

    return (
      <div className="productContainer">
        <div>
          <img src={this.props.productImage} alt="" />
          <h3>{this.props.productTitle}</h3>
          <p>{this.props.productDescription}</p>
          <p className="price">ALL {this.props.productPrice}</p>
          {productButtons}
        </div>
      </div>
    );
  }
}

export default Product;
