import React, { Component } from "react";
import Aux from "../hoc/Auxiliary";
import Product from "../../components/product/product";
import "./home.css";
import Axios from "axios";
import Spinner from "../ui/spinner/spinner";

class Store extends Component {
  state = {
    product: [null],
  };

  componentDidMount() {
    Axios.get("https://xpert-ecommerce.firebaseio.com/products.json").then(
      (response) => {
        this.setState({ product: response.data });
      }
    );
  }

  render() {
    let productList = <Spinner />;

    if (this.state.product) {
      productList = this.state.product.map((productItem) => (
        <Product
          productImage={productItem.item.img}
          productTitle={productItem.item.title}
          productDescription={productItem.item.description}
          productPrice={productItem.item.price}
        />
      ));
    }

    return (
      <Aux>
        <div>Slider</div>
        <div className="products">{productList}</div>
      </Aux>
    );
  }
}

export default Store;
